<!DOCTYPE html><html>	<head>		<meta charset="UTF-8">		<meta content="width=device-width,initial-scale=1.0,maximum-scale=1.0,user-scalable=no" name="viewport">		<meta content="yes" name="apple-mobile-web-app-capable">		<meta content="black" name="apple-mobile-web-app-status-bar-style">		<meta content="telephone=no" name="format-detection">		<meta content="email=no" name="format-detection">		<link rel="stylesheet" href="<?php echo e(asset('public/bus/css/common.css')) ?>">		<link rel="stylesheet" href="<?php echo e(asset('public/bus/css/bus.css')) ?>">	</head>	<body>		<div id="bus">			@yield('content')			<?php if($busAd): ?>			<a href="<?php echo e($busAd->url); ?>" class="ad">				<img src="<?php echo e(url('public/'.$busAd->image)); ?>" />			</a>			<?php endif; ?>			<div class="footer">				<a href="http://www.hiker-vip.com">技术支持 延边嗨科网络科技有限公司</a>				<p>数据提供 高德地图</p>			</div>		</div>	<script src="http://libs.baidu.com/jquery/1.8.3/jquery.min.js"></script>	<script type="text/javascript" src="https://webapi.amap.com/maps?v=1.4.14&key=ce393e0d42983ac6d71328a39c6c98e4"></script>	<script src="https://res.wx.qq.com/open/js/jweixin-1.2.0.js"></script>	<script>		var adcode = '<?php echo e($location['adcode']); ?>';		var city = '<?php echo e($location['city']); ?>';		wx.config({			debug: false,			appId: '<?php echo $signPackage["appId"];?>',			timestamp: '<?php echo $signPackage["timestamp"];?>',			nonceStr: '<?php echo $signPackage["nonceStr"];?>',			signature: '<?php echo $signPackage["signature"];?>',			jsApiList: [				'onMenuShareTimeline',				'onMenuShareAppMessage',				'checkJsApi',				'getLocation'			]		});		wx.ready(function () {			wx.onMenuShareAppMessage({				title: '公交查询',				desc: '市内公交路线，站点查询',				link: '<?php echo e(url('bus/line?partner_code='.session('partner_code'))); ?>',				imgUrl: '<?php echo e(url('public/bus/img/share.png')); ?>'			});			wx.onMenuShareTimeline({				title: '公交查询',				link: '<?php echo e(url('bus/line?partner_code='.session('partner_code'))); ?>',				imgUrl: '<?php echo e(url('public/bus/img/share.png')); ?>'			});			wx.getLocation({				success: function (res) {					var latitude = res.latitude;					var longitude = res.longitude;					var pos = [longitude, latitude];					setLocation(pos);				},				cancel: function () {					console.log('请手动输入我的位置')				}			});		});		/**		 * 根据坐标获取当前位置		 */		function setLocation (pos) {			AMap.convertFrom(pos, 'gps', function (status, result) {//腾讯坐标转换为高德坐标				if (result.info === 'ok') {					var lnglats = result.locations;					var startLngLat = [lnglats[0].lng, lnglats[0].lat];					AMap.plugin('AMap.Geocoder', function() {						var geocoder = new AMap.Geocoder();						geocoder.getAddress(startLngLat, function(status, result) {							if (status === 'complete' && result.info === 'OK') {								adcode = result.regeocode.addressComponent.adcode;								city = result.regeocode.addressComponent.city;								if($('input[name=org_name]') && $('input[name=org_name]').attr('path') === 'main'){									localStorage.setItem('org', lnglats[0].lng+','+lnglats[0].lat);									var addr = ((result.regeocode.formattedAddress).split(result.regeocode.addressComponent.township))[1];									$('input[name=org_name]').val(addr);								}							}						})					});				}			});		}	</script>	@yield('script')	</body></html>