@extends('bus.layout')@section('content')	<div class="station_view_head">		<h2></h2>		<p></p>		<a href="javascript:void(0);" onclick="changBus();"><img src="<?php echo e(url('public/bus/img/transfer.png')) ?>" /></a>	</div>	<div class="station_view"></div>@endsection@section('script')	<script>		var station_name = localStorage.getItem('station_name');		var bus_id = '<?php echo e(app('request')->input('bus_id')); ?>';		var _bus_ids = '<?php echo e(app('request')->input('bus_ids')); ?>';		var bus_ids = _bus_ids.split(',');		var index = $.inArray(bus_id, bus_ids);		$(function(){			$('.station_view_head h2').html(station_name);			getVia();		});		function getVia () {			AMap.plugin(["AMap.LineSearch"], function() {				var linesearch = new AMap.LineSearch({					pageIndex: 1,					pageSize: 1,					city: adcode,					extensions: "all"				});				linesearch.searchById(bus_id, function(status, result) {					if(status === 'complete'){						var via_list = result.lineInfo[0].via_stops;						var html  = '';						$('.station_view_head p').html(result.lineInfo[0].start_stop + ' - ' + result.lineInfo[0].end_stop);						$(via_list).each(function(k, v){							var dot = '';							var current_stop = (station_name.split('('))[0];							var stop = (v.name.split('('))[0];							if(current_stop === stop){								dot = 'dot';							}							html += '<p><span>'+v.name+'</span><em class="'+dot+'"></em></p>';						});						$('.station_view').html(html);					}				});			});		}		function changBus () {			var current = index + 1;			if(current >= bus_ids.length){				current = 0;			}			bus_id = bus_ids[current];			index = current;			getVia();		}	</script>@endsection