			'onMenuShareTimeline',				'onMenuShareAppMessage'			]		});		wx.ready(function (){			$(function(){				wx.onMenuShareAppMessage({					title: '<?php echo e($data->title); ?>',					desc: '<?php echo e($_SYSTEM['siteinfo']); ?>',					link: '<?php echo url('article/'.$data->id); ?>',					imgUrl: '<?php echo e(url('/public/'.$_SYSTEM['sharelogo'])); ?>',					success: function(res){					}				});				wx.onMenuShareTimeline({					title: '<?php echo e($data->title); ?>',					link: '<?php echo url('article/'.$data->id); ?>',					imgUrl: '<?php echo e(url('/public/'.$_SYSTEM['sharelogo'])); ?>',					success: function(res){						$.cookie('article-share-timeline-'+id, 1);						window.location.href = '<?php echo e(url('article/'.$data->id)); ?>';					}				});			});		});	</script>@endsection