on = parseInt('{{ $_SYSTEM['fiveday_on'] }}');			var fiveday_begin = parseInt('{{ $_SYSTEM['fiveday_begin'] }}');			var fiveday_space = parseInt('{{ $_SYSTEM['fiveday_space'] }}');			var fiveday_duration = parseInt('{{ $_SYSTEM['fiveday_duration'] }}');			var everyday = 86400;			var timestamp = Date.parse(new Date());			var now = timestamp / 1000;			var now_from_begin = now - fiveday_begin;			if (now > fiveday_on && now < fiveday_on + everyday) {				timeLeft = fiveday_on + everyday - now;			} else {				var fiveday_going = now_from_begin % ((fiveday_space + fiveday_duration) * everyday);				timeLeft = fiveday_duration * everyday - fiveday_going;			}			timer = window.setInterval(SetRemainTime, 1000);		});		function SetRemainTime() {			if (timeLeft > 0) {				timeLeft = timeLeft - 1;				var second = Math.floor(timeLeft % 60);				var minute = Math.floor((timeLeft / 60) % 60);				var hour = Math.floor((timeLeft / 3600) % 24);				var day = Math.floor((timeLeft / 3600) / 24);				$('#left_day').html(fill_0(day));				$('#left_hour').html(fill_0(hour));				$('#left_minute').html(fill_0(minute));				$('#left_second').html(fill_0(second));			} else {				window.clearInterval(timer);				window.location.reload();			}		}		function fill_0(num){			return num < 10 ? '0'+num : num;		}		wx.ready(function (){			wx.onMenuShareAppMessage({				title: '{{ $_SYSTEM['fiveday_title'] }}',				desc: '{{ str_replace(array("\r\n", "\r", "\n"), '', $_SYSTEM['fiveday_desc']) }}',				link: '<?php echo url('fiveday'); ?>',				imgUrl: '<?php echo e(url('public/event/img/fiveday_share.jpg')); ?>'			});			wx.onMenuShareTimeline({				title: '{{ $_SYSTEM['fiveday_title'] }}',				link: '<?php echo url('fiveday'); ?>',				imgUrl: '<?php echo e(url('public/event/img/fiveday_share.jpg')); ?>'			});		});	</script>@endsection