@extends('event.layout')@section('head')	<title>5日场-等待开放</title>@endsection@section('bg', 'class="white"')@section('content')	<div class="fiveday_off">		<?php if($bg): ?>		<a class="fiveday_off_bg" href="<?php echo e($bg->link); ?>">			<img src="<?php echo e(url('public/'.$bg->image)); ?>" alt="" />		</a>		<?php endif; ?>		<div class="fiveday_begin">			<img src="<?php echo e(url('public/event/img/fiveday.png')) ?>" alt="" />			<h2>距离开场剩余</h2>			<p>				<strong id="left_day">00</strong>				<span>天</span>				<strong id="left_hour">00</strong>				<span>时</span>				<strong id="left_minute">00</strong>				<span>分</span>				<strong id="left_second">00</strong>				<span>秒</span>			</p>		</div>	</div>@endsection@section('script')	<script>		var timeLeft;		var timer;		$(function(){			var fiveday_on = parseInt('{{ $_SYSTEM['fiveday_on'] }}');			var fiveday_begin = parseInt('{{ $_SYSTEM['fiveday_begin'] }}');			var fiveday_space = parseInt('{{ $_SYSTEM['fiveday_space'] }}');			var fiveday_duration = parseInt('{{ $_SYSTEM['fiveday_duration'] }}');			var everyday = 86400;			var timestamp = Date.parse(new Date());			var now = timestamp / 1000;			var now_from_begin = now - fiveday_begin;			var left_on = fiveday_on - now;			var next_fiveday_count = Math.ceil(now_from_begin / ((fiveday_space + fiveday_duration) * everyday));			var left_begin = next_fiveday_count * ((fiveday_space + fiveday_duration) * everyday) - now_from_begin;			if (left_on >= 0) {				timeLeft = Math.min(left_on, left_begin);			} else {				timeLeft = left_begin			}			timer = window.setInterval(SetRemainTime, 1000);		});		function SetRemainTime() {			if (timeLeft > 0) {				timeLeft = timeLeft - 1;				var second = Math.floor(timeLeft % 60);				var minute = Math.floor((timeLeft / 60) % 60);				var hour = Math.floor((timeLeft / 3600) % 24);				var day = Math.floor((timeLeft / 3600) / 24);				$('#left_day').html(fill_0(day));				$('#left_hour').html(fill_0(hour));				$('#left_minute').html(fill_0(minute));				$('#left_second').html(fill_0(second));			} else {				window.clearInterval(timer);				window.location.reload();			}		}		function fill_0(num){			return num < 10 ? '0'+num : num;		}		wx.ready(function (){			wx.onMenuShareAppMessage({				title: '{{ $_SYSTEM['fiveday_title'] }}',				desc: '{{ str_replace(array("\r\n", "\r", "\n"), '', $_SYSTEM['fiveday_desc']) }}',				link: '<?php echo url('fiveday'); ?>',				imgUrl: '<?php echo e(url('public/event/img/fiveday_share.jpg')); ?>'			});			wx.onMenuShareTimeline({				title: '{{ $_SYSTEM['fiveday_title'] }}',				link: '<?php echo url('fiveday'); ?>',				imgUrl: '<?php echo e(url('public/event/img/fiveday_share.jpg')); ?>'			});		});	</script>@endsection