 = date.getMonth() + 1;			var d = date.getDate();			var h = date.getHours();			var min = date.getMinutes();			return y + '-' + fillZero(m) + '-' + fillZero(d)+' '+fillZero(h)+':'+fillZero(min);		}		function fillZero(num){			return num >= 10 ? num : '0'+num;		}	</script>	@yield('script')	</body></html>